var n='@charset "UTF-8";\n/* stylelint-disable custom-property-pattern */\n/* stylelint-disable scss/dollar-variable-pattern */\n/* stylelint-disable selector-class-pattern */\n/* iOS 브라우저 높이 대응 */\n/* Gap */\n/* Spacing */\n/* Radius */\n/* Max Width */\n/*\n  Color Palette * Global\n  글로벌 변수에 정의된 것을 기준으로 시멘틱 컬러변수에 재정의하여 사용합니다.\n*/\n/* wemixplay */\n/* common */\n/* neutral */\n/* cool neutral */\n/* blue */\n/* red */\n/* green */\n/* orange */\n/* redOrange */\n/* lime */\n/* cyan */\n/* lightBlue */\n/* violet */\n/* purple */\n/* pink */\n/* opacity */\n/* ICON COLOR */\n/* Typography */\n/* 아래부터 4.0 리뉴얼 디자인 컬러값 적용 후 삭제 */\n/* stylelint-disable font-family-no-missing-generic-family-keyword */\n/* stylelint-disable order/properties-order */\n/* stylelint-disable scss/no-duplicate-mixins */\n/* 5120 NFT이미지 대응  */\n/* 768 미만은 모바일  */\n/* 1024 ~ */\n/* 1280 ~ */\n/* 1440 ~ */\n/* 1600 ~ */\n/* 1920 ~ */\n.radio-box {\n  /**\n  * position: relative; 를 바꾸게 되면 사이드이펙트가 날 수 있음으로\n  * 필요시 상위 element에 position 속성을 주시기 바랍니다.\n  */\n  position: relative;\n  display: inline-flex;\n  align-items: center;\n  cursor: pointer;\n  font-weight: 400;\n  letter-spacing: 0.0145em;\n  font-size: 14px;\n  font-size: 0.875rem;\n  line-height: 22px;\n  line-height: 1.375rem;\n}\n.radio-box.readonly {\n  cursor: not-allowed;\n  pointer-events: none;\n}\n.radio-box.disabled {\n  cursor: not-allowed;\n  opacity: 43%;\n}\n.radio-box.disabled .ico-check .icon {\n  border: 1px solid var(--wp-semantic-line-normal-normal);\n}\n@media (hover: hover) and (pointer: fine) {\n  .radio-box:not(.disabled):hover .ico-check::before {\n    opacity: 5%;\n  }\n}\n.radio-box:not(.disabled):focus .ico-check::before {\n  opacity: 8%;\n}\n.radio-box:not(.disabled):active .ico-check::before {\n  opacity: 12%;\n}\n.radio-box .ico-check {\n  position: relative;\n  display: flex;\n  width: 32px;\n  height: 32px;\n  align-items: center;\n  justify-content: center;\n  border-radius: 50%;\n  margin-right: 10px;\n  background-color: transparent;\n  transition: background-color 0.15s, border 0.15s;\n  /*\n    focus, active, hover 상태일 경우 체크아이콘의 interaction 영역에 표시되는 원형 배경색상\n  */\n}\n.radio-box .ico-check::before {\n  position: absolute;\n  top: 0;\n  left: 0;\n  width: 100%;\n  height: 100%;\n  border-radius: 50%;\n  background: var(--wp-semantic-label-normal);\n  content: "";\n  opacity: 0;\n  transition: opacity 0.15s;\n}\n.radio-box .ico-check .icon {\n  position: relative;\n  width: 20px;\n  height: 20px;\n  border: 1.5px solid var(--wp-semantic-line-normal-normal);\n  border-radius: 50%;\n  /*\n   * ::before checked아이콘의 색상부분\n   * ::after checked아이콘의 가운데 원형\n  */\n}\n.radio-box .ico-check .icon::before {\n  position: absolute;\n  top: 50%;\n  left: 50%;\n  width: 0;\n  height: 0;\n  border-radius: 50%;\n  background-color: var(--wp-semantic-primary-normal);\n  content: "";\n  transform: translate(-50%, -50%);\n  transition: width, height, 0.3s;\n}\n.radio-box .ico-check .icon::after {\n  position: absolute;\n  top: 50%;\n  left: 50%;\n  width: 0;\n  height: 0;\n  border-radius: 50%;\n  background-color: white;\n  content: "";\n  transform: translate(-50%, -50%);\n  transition: width, height, 0.3s;\n}\n.radio-box input {\n  display: none;\n}\n.radio-box input:checked ~ .ico-check .icon {\n  border: 0;\n}\n.radio-box input:checked ~ .ico-check .icon::before {\n  width: 100%;\n  height: 100%;\n}\n.radio-box input:checked ~ .ico-check .icon::after {\n  width: 8px;\n  height: 8px;\n}\n.radio-box input:checked ~ .ico-check::after {\n  transform: translateX(-50%) translateY(-50%) scale(1);\n}\n.radio-box .text {\n  /**\n  * 상위 선택자인 .checkbox의 스타일을 상속 받습니다.\n  */\n  color: inherit;\n  font-family: inherit;\n  font-feature-settings: inherit;\n  font-size: inherit;\n  font-weight: inherit;\n  line-height: inherit;\n  user-select: none;\n}\n.radio-box label {\n  /**\n  * label은 레이아웃에 영향을 받지 않는 마크업 구조로\n  * label을 클릭 했을시 하위 input에 대한 상태를 변경해주기 위해서만 존재하기 때문에\n  * display: contents; 속성을 주입하였습니다.\n  */\n  display: contents;\n  cursor: inherit;\n  /**\n  * label이 display: contents;이기 때문에 클릭 이벤트 또한 안먹는 구간이 있을 수 있으므로\n  * 클릭 이벤트는 먹을 수 있도록 가상 선택자를 활용하였습니다.\n  */\n}\n.radio-box label::after {\n  position: absolute;\n  z-index: 1;\n  top: 0;\n  left: 0;\n  display: block;\n  width: 100%;\n  height: 100%;\n  content: "";\n}\n.radio-box.small .ico-check {\n  width: 28px;\n  height: 28px;\n}\n.radio-box.small .ico-check .icon {\n  width: 16px;\n  height: 16px;\n}\n.radio-box.small input:checked ~ .ico-check .icon::after {\n  width: 6px;\n  height: 6px;\n}';export{n as default};
